// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract CertificateValidator {
    mapping(bytes32 => bool) private authenticCertificates;

    function addCertificate(bytes32 _hash) public {
        authenticCertificates[_hash] = true;
    }

    function isAuthentic(bytes32 _hash) public view returns (bool) {
        return authenticCertificates[_hash];
    }
}
